  if (!response.id) return [{ json: { success: false, operation: 'get_ticket_details', error: 'Ticket not found' } }];

  // Nettoyer le contenu HTML
  const cleanContent = (response.content || '').replace(/<[^>]*>/g, ' ').replace(/&nbsp;/g, ' ').replace(/\s+/g, ' ').trim();

  const statusMap = { 1: 'Nouveau', 2: 'En cours (attribu√©)', 3: 'En cours (planifi√©)', 4: 'En attente', 5: 'R√©solu', 6: 'Clos' };

  return [{ json: {
    success: true,
    operation: 'get_ticket_details',
    ticket: {
      id: response.id,
      title: response.name,
      content: cleanContent,
      status: statusMap[response.status] || response.status,
      status_code: response.status,
      priority: response.priority,
      urgency: response.urgency,
      impact: response.impact,
      date_creation: response.date_creation,
      date_mod: response.date_mod,
      entities_id: response.entities_id,
      users_id_recipient: response.users_id_recipient,
      requesttypes_id: response.requesttypes_id
