const input = $input.first().json;

// Validation
if (!input.user_id) throw new Error('user_id est requis');

// Construire le payload avec uniquement les champs fournis
const updateFields = { id: parseInt(input.user_id) };

if (input.realname !== undefined) updateFields.realname = input.realname;
if (input.firstname !== undefined) updateFields.firstname = input.firstname;
if (input.email !== undefined) updateFields.email = input.email;
if (input.phone !== undefined) updateFields.phone = input.phone;
